var $post_transport =$("#post_type")
var $token = $("#post_token")
$post_transport.change(function (){
    var $form = $(this).closest('form')
    var data ={}
    data[$token.attr('type')]=$token.val()
    data[$post_transport.attr('type')] = $post_transport.val()

    $.post($form.attr('action'), data).then(function (response)
    {
        $("#post_car").replaceWith(
            $(response).find("#post_car")
        )
        $("#post_uber").replaceWith(
            $(response).find("#post_car")
        )

    })
})
